    let [input,setInput] = useState([])

    let datas =[ ]

    let Submithandler = ((e)=>{

        e.preventDefault()

        if(!input.length){
            toast.error("Please enter something to submit")
        }else{
            console.log(input)
            input.map((item)=>{


                if(item.length > 8){
                    datas.push(item)


                }


            })

            console.log(datas)



    }



    })

    let handlerOnChange = ((e)=>{

            setInput((input) => [...input, e.target.value])

        })

  return (
    <>
            <ToastContainer position='center' />
        <h1 style={{textAlign:"center"}}>Welcom Enter name</h1>
        <div className='parent'> 
    <div>

    </div>

        <div>

        <form  onSubmit={(e)=>Submithandler(e)}>
  <label>
    Name:
    <input onChange={(e)=>  handlerOnChange(e)
} type="text" name="name" />
  </label>
  <input  type="submit" value="Submit" />
</form>
        </div>
{
    datas.length && datas.map((item)=>(
        <p>
            {item}

        </p>

    ))
}
    </div>
    </>


  )
}

export default Add
