import React, { useEffect, useState } from 'react'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Button } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
const Edit = ({datas}) => {
  let [dats,setdats] = useState([])
  let [remove ,setremove] = useState(null)
  let [tick ,settick] = useState([])
  let [tickarr ,settickarr] = useState([])

  useEffect(()=>{
    let  datasarray = []
    let dataS = localStorage.getItem("todo")
    var nameArr = dataS.split(',');
    datasarray.push(nameArr)

     setdats(nameArr)


  },[])

  useEffect(()=>{
    if(remove!==null){
      const result = dats.filter(word => word!== remove);
      localStorage.setItem("todo",result)
      toast.error("succesfully deleted")
      window.location.href="/edit"
    }



  },[remove])
  useEffect(()=>{

    let  datasarray = []
    let dataS = localStorage.getItem("tick")
    var nameArr = dataS.split(',');
    datasarray.push(nameArr)

     setdats(nameArr)


  },[])
  let clickHandler = ((e)=>{
    settick(tick => [...tick,e.target.value]);
    localStorage.setItem("tick",tick)

   })
  console.log(tick)
  return (
    <div style={{marginLeft:"20%"}}> 
                <ToastContainer position='center' />

      <h1>Todo list</h1>
      {dats &&dats.map((items)=>(
        items !== '' &&
        <div >
           <p>
          {items}
          &nbsp;&nbsp;&nbsp;
          <input type="checkbox" value={items} onClick={(e)=>clickHandler(e)} />

          <Button style={{marginLeft:"200px"}} variant="danger" onClick={()=>{setremove(items)}}>Delete</Button>{' '}
          <hr></hr>
        </p>


        </div>

      ))}
      <div>
        <h1>Checkbox</h1>
      {dats &&dats.map((items)=>(
        items !== '' &&
        <div>
           <p>
          {items}
          &nbsp;&nbsp;&nbsp;
          {tick && tick.length && tick == items ?          <input type="checkbox" defaultChecked value={`${items}`}/>
:          <input type="checkbox" value={`${items}`}/>
}      <Button style={{marginLeft:"200px"}}  variant="danger" onClick={()=>{setremove(items)}}>Delete</Button>{' '}

        </p>


        </div>

      ))}
      </div>
    </div>
  )
}

export default Edit
