import React, { useEffect, useState } from 'react'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
const Edit = ({datas}) => {
  let [dats,setdats] = useState([])
  let [remove ,setremove] = useState('')

  useEffect(()=>{
    let  datasarray = []
    let dataS = localStorage.getItem("todo")
    var nameArr = dataS.split(',');
    datasarray.push(nameArr)

     setdats(nameArr)


  },[])

  useEffect(()=>{
    if(remove!==null){
      const result = dats.filter(word => word!== remove);
      localStorage.setItem("todo",result)
      toast.error("succesfully deleted")
      window.location.href="/edit"
    }



  },[remove])
  let clickHandler = ((e)=>{
    let tick = []
    tick.push(e.target.value)
    console.log(tick)

   })
  return (
    <div style={{marginLeft:"50%"}}> 
                <ToastContainer position='center' />

      <h1>Todo list</h1>
      {dats &&dats.map((items)=>(
        items !== '' &&
        <div>
           <p>
          {items}
          &nbsp;&nbsp;&nbsp;
          <input type="checkbox" onClick={(e)=>{clickHandler(remove => [...remove, e.target.value]);}} />

          <button onClick={()=>{setremove(items)}}>
          Delete
        </button>  
        </p>


        </div>

      ))}
      <div>
        <h1>Edit</h1>
      {dats &&dats.map((items)=>(
        items !== '' &&
        <div>
           <p>
          {items}
          &nbsp;&nbsp;&nbsp;
          <input type="checkbox" value={`${items}`}/>
          <button onClick={()=>{setremove(items)}}>
          Delete
        </button>  
        </p>


        </div>

      ))}
      </div>
    </div>
  )
}

export default Edit
