import React, { useEffect, useState } from 'react'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Button } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
  import Modal from 'react-modal';

const Edit = ({datas}) => {
  let subtitle;
  const [modalIsOpen, setIsOpen] = React.useState(false);

  function openModal() {
    setIsOpen(true);
  }

  function afterOpenModal() {
    // references are now sync'd and can be accessed.
    subtitle.style.color = '#f00';
  }

  function closeModal() {
    setIsOpen(false);
  }
  let [dats,setdats] = useState([])
  let [remove ,setremove] = useState(null)
  let [tick ,settick] = useState([])
  let [edit,setedit] = useState([])
  let [editInput,setEdit] = useState([])

  useEffect(()=>{
    let  datasarray = []
    let dataS = localStorage.getItem("todo")
    var nameArr = dataS.split(',');
    datasarray.push(nameArr)

     setdats(nameArr)


  },[])

  useEffect(()=>{
    if(remove!==null){
      const result = dats.filter(word => word!== remove);
      localStorage.setItem("todo",result)
      toast.error("succesfully deleted")
      window.location.href="/edit"
    }



  },[remove])


  let edits = ((e)=>{
    e.preventDefault()

       const result = dats.filter(word => word!== edit);
      localStorage.setItem("todo",result)
      toast.success("succesfully Edited")
      // window.location.href="/edit"


  })
  // let clickHandler = ((e)=>{
  //   let tick = []
  //   tick.push(e.target.value)
  //   console.log(tick)

  //  })

const customStyles = {
  content: {
    top: '50%',
    left: '50%',
    right: 'auto',
    bottom: 'auto',
    marginRight: '-50%',
    transform: 'translate(-50%, -50%)',
  },
};
console.log(edit)

  return (
    <div style={{marginLeft:"20%"}}> 
     <div>
      <Modal
        isOpen={modalIsOpen}
        onAfterOpen={afterOpenModal}
        onRequestClose={closeModal}
        style={customStyles}
        contentLabel="Example Modal"
      >
        <h2 ref={(_subtitle) => (subtitle = _subtitle)}>Edit</h2>
        <button onClick={closeModal}>close</button>
          <input  onChange={(e)=>{setEdit(e.target.value)}}/>
          <button type='button' onClick={(e)=>{edits(e)}}> Submit</button>
      </Modal>
    </div>
                <ToastContainer position='center' />

      <h1>Todo list</h1>
      {dats &&dats.map((items)=>(
        items !== '' &&
        <div >
           <p>
          {items}
          &nbsp;&nbsp;&nbsp;
          <input type="checkbox" value={items} onClick={(e)=>{settick(tick => [...tick,e.target.value]);}} />
          <button style={{marginLeft:"200px"}} onClick={()=>[openModal(),setedit(items)]}>Edit</button>

          <Button style={{marginLeft:"200px"}} variant="danger" onClick={()=>{setremove(items)}}>Delete</Button>{' '}
          <hr></hr>
        </p>


        </div>

      ))}
      <div>
        <h1>Checkbox</h1>
      {dats &&dats.map((items)=>(
        items !== '' &&
        <div>
           <p>
          {items}
          &nbsp;&nbsp;&nbsp;
          {tick && tick.length && tick.includes(items) ?          <><input type="checkbox" defaultChecked value={`${items}`} /><span style={{marginLeft:"10px"}}>completed</span></>
:          <input type="checkbox" value={`${items}`}/>
}      <Button style={{marginLeft:"200px"}}  variant="danger" onClick={()=>{setremove(items)}}>Delete</Button>{' '}

        </p>


        </div>

      ))}
      </div>
    </div>
  )
}

export default Edit
